/* Enemy generator
 */

var EnemyGenerator = function(gameRef, container, left, top, right, bottom) {

	this.gameRef = gameRef;
	this.left = left;
	this.top = top;
	this.right = right;
	this.bottom = bottom;
	this.container = container;
	this.enabled = true;

	this.speed = 0.01;

	// initial timer leaves some space for tutorial
	this.timer = 120 +(this.gameRef.isTutorial ? 250 : 0);
	this.total_timer = 0;
	this.enemies = [];

	this.create();
};

EnemyGenerator.prototype = {

	create : function() {

	},
	
	destroy : function() {

		for (var i = 0 ; i < this.enemies.length; i++) {
			this.container.removeChild(this.enemies[i].sprite);
			this.enemies[i].destroy();
			this.enemies[i] = null;
		}
		this.enemies = null;

	},

	update : function (dt, side) {

		this.total_timer += 1 *dt;

		this.speed = 0.01 +(this.total_timer /2000 *0.01);
		if (this.speed > 0.03) this.speed = 0.03;
		//

		// When timer reaches 0, make a new enemy
		this.timer -= 1 *dt;
		if (this.timer <= 0 && this.enabled) {
			
			// Randomise between 2 sprites
			var sp;
			var anim;
			var rand = Math.random();
			if (rand < 0.33) {
				sp = "bball_lucia";
				anim = "run";
			}
			else
			if (rand < 0.66) {
				sp = "bball_makayla";
				anim = "run";
			}
			else {
				sp = "bball_tammy";
				anim = "run_tammy";
			}

			var enemy = new BasketballPlayer(this.container, sp, anim, true);
			enemy.sprite.y = GAME.maxHeight/4;

			/* Randomise lane, either left or right
			 * the `dif` variable below makes enemies more likely to spawn where the ball is
			 */
			var dif = 0.5 +(this.gameRef.ball_side == 0 ? -0.2 : 0.2);
			if (Math.random() < dif) {
				enemy.sprite.x = this.right;
				enemy.side = 1;
			}
			else {
				enemy.sprite.x = this.left;
				enemy.side = 0;
			}

			enemy.sprite.scale.x = 1;

			// Set target for animation
			enemy.start_x = enemy.sprite.x;
			enemy.start_y = enemy.sprite.y;
			enemy.target_x = enemy.sprite.x;
			enemy.target_y = this.bottom -enemy.sprite.height/2;
			enemy.inter = 0;

			enemy.speed = this.speed;

			// Add enemy to array
			this.enemies.push(enemy);

			// How much time until next enemy
			this.timer = 200 -(this.total_timer /20) +Math.random() *(50 -(this.total_timer /20));
			if (this.timer < 50) this.timer = 50;
		}

		// Update enemies
		for (var i = 0; i < this.enemies.length; i++) {

			// Update animations
			this.enemies[i].update(dt);

			//if (!this.enabled && !this.enemies[i].steal) continue;

			this.enemies[i].inter += this.enemies[i].speed *dt;

			// Cap interpolation animation
			if (this.enemies[i].inter > 1) this.enemies[i].inter = 1;

			// Move enemy
			this.enemies[i].sprite.x = this.enemies[i].start_x +(this.enemies[i].target_x -this.enemies[i].start_x) *this.enemies[i].inter;
			this.enemies[i].sprite.y = this.enemies[i].start_y +(this.enemies[i].target_y -this.enemies[i].start_y) *this.enemies[i].inter;

			// Enemy failed to grab ball, so its running away from the screen, when that's complete, remove them
			if (this.enemies[i].runaway) {
				if (this.enemies[i].inter == 1) {
					this.enemies[i].destroy();
					this.enemies.splice(i, 1);
				}
			}
			else
			if (this.enemies[i].steal) {
				if (this.enemies[i].steal_state == 1 && this.enemies[i].inter > 0.7/*1*/) {
					var offset = 100;
					this.enemies[i].steal_state = 2;
					this.enemies[i].inter = 0;
					this.enemies[i].start_x = this.enemies[i].sprite.x;
					this.enemies[i].start_y = this.enemies[i].sprite.y;
					this.enemies[i].target_x = this.enemies[i].side == 0 ? this.left +offset : this.right -offset;
					this.enemies[i].target_y = this.bottom +this.enemies[i].sprite.height;

					if (!this.gameRef.ball.grabbedBy) {
						this.gameRef.ball.grabbedBy = this.enemies[i];
					}
					if (this.gameRef.ball_side == 0) {
						this.gameRef.player_left.setAnim("run_no_ball");
					}
					else {
						this.gameRef.player_right.setAnim("run_no_ball");
					}
				}
			}
			else
			// Enemy just reached player
			if (this.enemies[i].inter == 1) {
				// Enemy is on the same side as the ball
				if (side == this.enemies[i].side) {
					/*
					this.enemies[i].destroy();
					this.enemies.splice(i, 1);
					*/
					if (!this.gameRef.isGameOver) {
						this.gameRef.showGameOver();
					}

					var offset = 100;
					this.enemies[i].steal = true;
					this.enemies[i].steal_state = 1;
					this.enemies[i].inter = 0;
					this.enemies[i].start_x = this.enemies[i].sprite.x;
					this.enemies[i].start_y = this.enemies[i].sprite.y;
					this.enemies[i].target_x = this.enemies[i].side == 0 ? this.left +offset : this.right -offset;
					this.enemies[i].target_y = this.bottom;

					this.enemies[i].speed = 0.03;
				}
				/* Enemy is on the wrong side, they miss
				 * in this case, make them run away, and animate them away from the screen
				 */
				else {
					var offset = 200;
					this.enemies[i].runaway = true;
					this.enemies[i].inter = 0;
					this.enemies[i].start_x = this.enemies[i].sprite.x;
					this.enemies[i].start_y = this.enemies[i].sprite.y;
					this.enemies[i].target_x = this.enemies[i].side == 0 ? this.left -offset : this.right +offset;
					this.enemies[i].target_y = this.bottom +this.enemies[i].sprite.height;
				}
			}

		} // Update enemies
	} // Update
} // Enemy generator


