	/* screen is clicked */
	this.onScreenButtonDown = function(event) {

		// Tutorial is active
		if(_self.isTutorial)
		{
			// Tutorial is active and waiting input
			if(_self.tutorial_counter > _self.tutorial_delay[_self.tutorial_state]){
				//Move to the next state
				//Incriments current state
				_self.tutorial_state++;
				//Sets counter timer back to 0
				_self.tutorial_counter = 0;

				//If no more tutorial states, end tutorial controls
				if(_self.tutorial_state >= _self.tutorial_delay.length){
					_self.isTutorial = false;
				}

				//Hide tut text and pointy finger
				_self.tutorial_text.visible = false;
				_self.tutorialPointer.visible = false;

				//If on last state, do not pass ball
				if(_self.tutorial_state == 1 || _self.tutorial_state == 3) {
					return;
				}
			}
			// Not waiting for input - discard it
			else {
				return;
			}
		}

		/* When a player is still catching a ball or the game is over, we do not expect further input
		 * the `animProgress` check is done to allow throw the ball before the catch animation ends
		 */
		if ((_self.player_left .currentAnim == "catch" && _self.player_left .animProgress < 0.7)
		||  (_self.player_right.currentAnim == "catch" && _self.player_right.animProgress < 0.7)
		|| _self.isGameOver) return;

		// TODO: Pass sound
		GAME.assetManager.play("whoosh");

		/* Ball is on the left player
		 * left player throws ball
		 * right player catches ball
		 */
		if (_self.ball_side == 0) {
			_self.player_left.setAnim("throw");
			_self.player_right.setAnim("catch");
			_self.ball_side = 1;

			_self.ball.start_x = _self.player_left.sprite.x;
			_self.ball.start_y = _self.player_left.sprite.y -100;
			_self.ball.target_x = _self.player_right.sprite.x - (_self.ball.width / 2) - 10;
			_self.ball.target_y = _self.player_right.sprite.y -100;
		}
		/* Ball is on the right player
		 * right player throws ball
		 * left player catches ball
		 */
		else {
			_self.player_left.setAnim("catch");
			_self.player_right.setAnim("throw");
			_self.ball_side = 0;

			_self.ball.start_x = _self.player_right.sprite.x - (_self.ball.width / 2) - 10;
			_self.ball.start_y = _self.player_right.sprite.y -100;
			_self.ball.target_x = _self.player_left.sprite.x;
			_self.ball.target_y = _self.player_left.sprite.y -100;
		}

	}

