var BasketballPlayer = function(container, anims, first_anim, on_top) {

	// IE6 shenanigans
	if (first_anim === undefined) {
		first_anim = "run_no_ball";
	}

	if (on_top === undefined) {
		on_top = false;
	}

	this.container = container;

	//particles
	this.emitter = null;

	// graphics container
	this.sprite = null;
	
	// graphic elements
	this.armatureDisplay = null;

	this.shadow = null;
	
	// states
	this.isMoving = false;
	this.isDying = false;
	this.isKilled = false;
	
	this.animProgress = 0;
	this.currentAnim = '';

	this.isRunning = true;

	this.anims = anims;

	this.on_top = on_top
	this.first_anim = first_anim;
	
	this.create();
};

BasketballPlayer.prototype = {

	create : function() {

		this.destroy();
		
		this.sprite = new PIXI.Container();
		
		if (this.on_top) {
			this.container.addChildAt(this.sprite, 0);
		}
		else {
			this.container.addChild(this.sprite);
		}
			
		
		// Shadow
		this.shadow = new PIXI.Graphics();
		this.shadow.beginFill(0, 0.3);
		this.shadow.drawEllipse(this.sprite.x, this.sprite.y, 40, 15);
		this.shadow.endFill();

		// Yes this is real, your eyes do not deceive you
		// Apparently if the last thing drawn with graphics has less transparency than 1
		// it causes other objects (I suspect drawn after this object) to be parcially transparent as well
		// ONLY ON ONE DEVICE - NEXUS 7 !!!
		// By drawing a 1x1 elipse outside of the screen area with full transparency, solves this issue
		this.shadow.beginFill(0, 1);
		this.shadow.drawEllipse(0, 0, 1, 1);
		this.shadow.endFill();

		this.sprite.addChild(this.shadow);

		this.initAnim(this.anims);

		this.vel = new Vector2D(0, 0);
		this.velNormal = new Vector2D(0, 0);
	},
	
	destroy : function() {
		
		if (this.sprite) {
			
			if (this.armatureDisplay) {
				
				this.sprite.removeChild(this.armatureDisplay);
				this.armatureDisplay.off(dragonBones.EventObject.LOOP_COMPLETE, this.onAnimationComplete, this);
				this.armatureDisplay = null;
			}
			
			this.sprite.destroy();
			this.sprite = null;
		}

		this.vel = null;
		this.velNormal = null;
	},
	
	initAnim : function(id) {
		
		var factory = dragonBones.PixiFactory.factory;
		
		if (!factory._dragonBonesDataMap[id]) {
			
			factory.parseDragonBonesData(GAME.animData[0].resources[id + '_skeleton'].data);
			factory.parseTextureAtlasData(GAME.animData[0].resources[id + '_data'].data, GAME.animData[0].resources[id + '_atlas'].texture);
		}
        
		this.armatureDisplay = factory.buildArmatureDisplay(id);
		this.armatureDisplay.scale.set(1.25);
		
		this.armatureDisplay.on(dragonBones.EventObject.LOOP_COMPLETE, this.onAnimationComplete, this);
		this.setAnim(this.first_anim);
		
		this.sprite.addChild(this.armatureDisplay);
	},
	
	setAnim : function(anim) {
		if (this.currentAnim == "fail") return;
		
		if (this.armatureDisplay) {
			
			if (this.currentAnim != anim) {
				
				this.armatureDisplay.animation.play(anim);
				this.currentAnim = anim;
				
				this.armatureDisplay.animation.stop();
				this.animProgress = 0;
			}
		}

	},
	
	setFrame : function(time) {
		
		if (this.armatureDisplay) {
			
			if (this.armatureDisplay.animation._animations[this.currentAnim]) {
				
				if (time >= this.armatureDisplay.animation._animations[this.currentAnim].duration) {
					
					if (this.currentAnim == 'catch' || this.currentAnim == 'throw') {
						
						time = 0;
						this.onAnimationComplete();
					}	
					this.animProgress = 0;
				}
			}
			
			this.armatureDisplay.animation.gotoAndStopByTime(this.currentAnim, time);
		}
	},
	
	onAnimationComplete : function(event) {
		
		if (this.armatureDisplay) {
			
			if (this.currentAnim == 'catch') {
				this.setAnim('run_ball');
			}
			else
			if (this.currentAnim == 'throw') {
				if (this.catchAction) {
					this.catchAction();
				}
				this.setAnim('run_no_ball');
			}
		}
	},
	
	pause : function() {
		
		this.isRunning = false;
		
		if (this.armatureDisplay) {
			
			
		}
	},
	
	resume : function() {
		
		this.isRunning = true;
		
		if (this.armatureDisplay) {
			
			
		}
	},
	
	hit : function(objectType) {

		
	},
	
	update : function (dt) {
		
		if (this.isRunning) {

			this.animProgress += dt * 0.02;
			this.setFrame(this.animProgress);

		}		
	},

	setCatchAction : function(action) {
		this.catchAction = action;
	}
};

/* Scrolling background
 */

var ScrollingBackground = function(container, left, top, right, bottom) {

	this.left = left;
	this.top = top;
	this.right = right;
	this.bottom = bottom;
	this.container = container;

	this.scrolling_speed = 2;

	var x = this.left;
	var y = this.bottom;
	this.array = [];

	var sprite = PIXI.Sprite.from(GAME.assetManager.get('bb_floor_section'), false);

	// Add all tiles in a 2D array
	var height = 0;
	while (y > this.top -sprite.height) {

		var ar = [];
		
		x = this.left;
		while (x < this.right) {
	
			var sprite_name;

			if (y > this.top) {

				if (x == this.left) {

					sprite_name = 'bb_floor_section_line_left';
				}
				else if (x + sprite.width > this.right) {

					sprite_name = 'bb_floor_section_line_right';
				}
				else {

					sprite_name = 'bb_floor_section_line_centre';
				}
			}
			else {

				if (x == this.left || x + sprite.width > this.right) {
					
					sprite_name = 'bb_floor_section_lines';
				}
				else {

					sprite_name = 'bb_floor_section';
				}
			}

			var spriteTile = PIXI.Sprite.from(GAME.assetManager.get(sprite_name), false);
			spriteTile.x = x;
			spriteTile.y = y;
			container.addChild(spriteTile);
	
			ar.push(spriteTile);
	
			x += spriteTile.width;
			height = spriteTile.height;
	
		}

		this.array.push(ar);
		y -= spriteTile.height;
	}

	// Offset tiles so they focus in the centre
	if (x > this.right) {
		var offset = (x -this.right)/2;
		for (var i = 0; i < this.array.length; i++) {
			for (var j = 0; j < this.array[i].length; j++) {
				this.array[i][j].x -= offset;
			}
		}
	}

	// Objects
	this.timer = 10;
	this.objects = [];

	this.create();
};

ScrollingBackground.prototype = {

	create : function() {

	},
	
	destroy : function() {
		for (var i = 0; i < this.array.length; i++) {
			for (var j = 0; j < this.array[i].length; j++) {
				this.container.removeChild(this.array[i][j]);
				this.array[i][j].destroy();
				this.array[i][j] = null;
			}
			this.array[i] = null;
		}
		this.array = null
	},
	
	update : function (dt) {

		// Advance timer
		this.timer -= 0.1;

		// Time to make a new object
		if (this.timer < 0) {

			// Basketball
			var basketball = PIXI.Sprite.from(GAME.assetManager.get('ball', false));
			basketball.x = GAME.maxWidth/4 -basketball.width*3/4 +(Math.random() *(basketball.width*3/4));
			if (Math.random() > 0.5) basketball.x += GAME.maxWidth/2;
			basketball.y = GAME.maxHeight/4 -basketball.height;
			basketball.scale.x /= 1.5;
			basketball.scale.y /= 1.5;
			basketball.tint = 0x888888;

			// Shadow
			var ball_shadow = new PIXI.Graphics();
			ball_shadow.beginFill(0, 0.3);
			ball_shadow.drawEllipse(basketball.x +basketball.width/2, basketball.y +basketball.height, 25, 8);
			ball_shadow.endFill();
			ball_shadow.addChild(basketball);

			this.container.addChild(ball_shadow);
			this.objects.push(ball_shadow);

			// Reset timer
			this.timer = 10 +Math.random() *20;

		}

		// Scroll objects
		for (var i = 0; i < this.objects.length; i++) {
			this.objects[i].y += this.scrolling_speed *dt;
			if (this.objects[i].y > this.bottom) {
				this.objects[i].destroy();
				this.objects.splice(i, 1);
			}
		}

		// Floor
		for (var i = 0; i < this.array.length; i++) {
			for (var j = 0; j < this.array[i].length; j++) {
				this.array[i][j].y += this.scrolling_speed *dt;
				if (this.array[i][j].y > this.bottom) {
					this.array[i][j].y -= this.array[i][j].height *this.array.length;
				}
			}
		}


	}

}

