/*
 * Standard Button
 * DD 2017
 */

var Button = function(idleImg, overImg, downImg, onPress, scope) {

	// button id is useful
	this.id = 0;
	
	// on pressed function
	this.onPress = onPress;
	this.onDown = null;
	this.onMove = null;
	
	// function scope
	this.scope = scope;
	
	// graphics container
	this.sprite = null;
	
	// state textures
	this.textureButtonIdle = null;
	this.textureButtonDown = null;
	this.textureButtonOver = null;
	
	// icon image
	this.icon = null;
	this.iconYIdle = 0;
	this.iconYOver = 0;
	
	// enabled flag
	this.isEnabled = true;
	
	// highlight
	this.highlight = null;
	
	// voice over file
	this.voice = null;
	this.timeout = null;
	this.isVoiceOnTabOnly = false;
	
	this.create(idleImg, overImg, downImg);
};

Button.prototype = {

	create : function(idleImg, overImg, downImg) {
		
		this.destroy();
		
		this.textureButtonIdle = PIXI.Texture.from(GAME.assetManager.get(idleImg, false));
		this.textureButtonDown = PIXI.Texture.from(GAME.assetManager.get(downImg, false));
		this.textureButtonOver = PIXI.Texture.from(GAME.assetManager.get(overImg, false));
		
		this.sprite = new PIXI.Sprite(this.textureButtonIdle);
		this.sprite.buttonMode = true;
		this.sprite.interactive = true;
		this.sprite.anchor.set(0.5);
		
		this.sprite
			.on('pointerdown', this.onButtonDown)
			.on('pointerup', this.onButtonUp)
			.on('pointerupoutside', this.onButtonUpOutside)
			.on('pointerover', this.onButtonOver)
			.on('pointerout', this.onButtonOut)
			.on('pointermove', this.onButtonMove);
		
		// set a ref to this scope on the sprite
		this.sprite.button = this;
		
		this.highlight = PIXI.Sprite.from(GAME.assetManager.get('button_highlight', false));
		this.highlight.buttonMode = false;
		this.highlight.interactive = false;
		this.highlight.anchor.set(0.5);
		this.highlight.visible = false;
		this.sprite.addChild(this.highlight);
		
		//
	},
	
	destroy : function() {
		
		if (this.icon) {
			
			this.sprite.removeChild(this.icon);
			this.icon.destroy({children:true, texture:true});
			this.icon = null;
		}
		
		if (this.highlight) {
			
			this.sprite.removeChild(this.highlight);
			this.highlight.destroy({children:true, texture:true});
			this.highlight = null;
		}
		
		if (this.sprite) {
			
			this.sprite
				.off('pointerdown', this.onButtonDown)
				.off('pointerup', this.onButtonUp)
				.off('pointerupoutside', this.onButtonUpOutside)
				.off('pointerover', this.onButtonOver)
				.off('pointerout', this.onButtonOut)
				.off('pointermove', this.onButtonMove);
			
			this.sprite.destroy({children:true, texture:true});
			this.sprite = null;
		}
		
		clearTimeout(this.timeout);
		this.voice = null;
		
		this.textureButtonIdle = null;
		this.textureButtonDown = null;
		this.textureButtonOver = null;
	},
	
	// this lets us reuse base button states for optimisation
	// but limits the visual style so may not always be appropriate
	addIcon : function(icon, idleY, overY) {
		
		this.icon = PIXI.Sprite.from(GAME.assetManager.get(icon, false));
		this.icon.buttonMode = false;
		this.icon.interactive = false;
		this.icon.anchor.set(0.5);
		this.sprite.addChild(this.icon);
		
		this.iconYIdle = idleY;
		this.iconYOver = overY;
		
		this.icon.y = this.iconYIdle;
	},

	setVO : function(file, tabOnly) {
		
		this.voice = file;
		this.isVoiceOnTabOnly = false || tabOnly;
	},
	
	select : function() {

		if (this.voice && !this.isVoiceOnTabOnly) {
				
			GAME.assetManager.play(this.voice);
		}
		
		// do the thing!
		if (this.onPress) {
			
			this.onPress();
		}
	},

	onButtonDown : function(event) {
		
		if (!event.currentTarget.button.isEnabled) return;
		
		//this.isOver = true;
		this.isDown = true;
		this.texture = event.currentTarget.button.textureButtonDown;
		
		GAME.vibrate(250);
		
		// if there is an icon on this button - set the  position
		if (event.currentTarget.button.icon) {
			
			event.currentTarget.button.icon.y = event.currentTarget.button.iconYOver;
		}
		
		// down handler set?
		if (event.currentTarget.button.onDown) {
			
			event.currentTarget.button.onDown(event);
		}
	},
	
	onButtonUp : function(event) {
		
		clearTimeout(event.currentTarget.button.timeout);
		
		if (!event.currentTarget.button.isEnabled) return;
		
		this.isDown = false;
		
		if (this.isOver) {
			
			this.texture = event.currentTarget.button.textureButtonOver;
			
			if (event.currentTarget.button.icon) {
				
				event.currentTarget.button.icon.y = event.currentTarget.button.iconYOver;
			}
		}
		else {
			
			this.texture = event.currentTarget.button.textureButtonIdle;
			
			if (event.currentTarget.button.icon) {
				
				event.currentTarget.button.icon.y = event.currentTarget.button.iconYIdle;
			}
			
			// only play vo if hasn't already played on over
			if (event.currentTarget.button.voice && !event.currentTarget.button.isVoiceOnTabOnly) {
				
				GAME.assetManager.play(event.currentTarget.button.voice);
			}
		}
		
		// do the thing!
		if (event.currentTarget.button.onPress) event.currentTarget.button.onPress(event);
		//GAME.assetManager.play("click");
	},
	
	onButtonUpOutside : function(event) {
		
		clearTimeout(event.currentTarget.button.timeout);
		
		if (!event.currentTarget.button.isEnabled) return;
		
		this.isDown = false;
		this.isOver = false;
		
		this.texture = event.currentTarget.button.textureButtonIdle;
		
		if (event.currentTarget.button.icon) {
			
			event.currentTarget.button.icon.y = event.currentTarget.button.iconYIdle;
		}
	},
	
	onButtonOver : function(event) {
		
		if (!event.currentTarget.button.isEnabled) return;
		
		this.isOver = true;
		
		if (!this.isDown) {
			
			this.texture = event.currentTarget.button.textureButtonOver;
			
			if (event.currentTarget.button.icon) {
				
				event.currentTarget.button.icon.y = event.currentTarget.button.iconYOver;
			}
			
			if (event.currentTarget.button.voice && !event.currentTarget.button.isVoiceOnTabOnly) {
				
				// play voice after 1 second
				var voice = event.currentTarget.button.voice;
				event.currentTarget.button.timeout = setTimeout(function(){ GAME.assetManager.play(voice) }, 750);
			}
		}
	},
	
	onButtonOut : function(event) {
		
		clearTimeout(event.currentTarget.button.timeout);
		
		if (!event.currentTarget.button.isEnabled) return;
		
		this.isOver = false;
		
		if (!this.isDown) {
			
			this.texture = event.currentTarget.button.textureButtonIdle;
			
			if (event.currentTarget.button.icon) {
				
				event.currentTarget.button.icon.y = event.currentTarget.button.iconYIdle;
			}
		}
	},
	
	onButtonMove : function(event) {
		
		if (!event.currentTarget.button.isEnabled) return;
		
		// move handler set?
		if (event.currentTarget.button.onMove) {
			
			event.currentTarget.button.onMove(event);
		}
	},
	
	setOnDown : function(callback) {
		
		// set an optional onDown callback
		this.onDown = callback;
	},
	
	setOnMove : function(callback) {
		
		// set an optional onDown callback
		this.onMove = callback;
	},
	
	reset : function() {
		
		this.sprite.isOver = false;
		this.sprite.isDown = false;
		
		this.sprite.texture = this.textureButtonIdle;
		
		if (this.icon) {
			
			this.icon.y = this.iconYIdle;
		}
	},
	
	enable : function() {
		
		this.isEnabled = true;
		this.sprite.buttonMode = true;
		this.sprite.interactive = true;
		this.sprite.alpha = 1;
	},
	
	disable : function() {
		
		this.isEnabled = false;
		this.sprite.buttonMode = false;
		this.sprite.interactive = false;
		this.sprite.alpha = 0.3;
	},
	
	highlightOn : function() {
		
		this.highlight.visible = true;
	},
	
	highlightOff : function() {
		
		this.highlight.visible = false;
	},
	
	setTextures : function(idleImg, overImg, downImg) {
		
		this.textureButtonIdle = PIXI.Texture.from(GAME.assetManager.get(idleImg, false));
		this.textureButtonDown = PIXI.Texture.from(GAME.assetManager.get(downImg, false));
		this.textureButtonOver = PIXI.Texture.from(GAME.assetManager.get(overImg, false));
		
		this.sprite.texture = this.textureButtonOver;
	}
};
/*
 * Camera class 
 * Ported from Haxe
 * David Durham 2017
 */

var Camera = function(w, h) {

	// scene dimensions
	this.halfWidth;
	this.halfHeight;
	
	// camera 'look at' point
	this.lookAtX;
	this.lookAtY;
	
	// movement damping
	this.damping = 20;
	
	// camera zoom
	this.zoom = 1;
	this.rZoom = 1;
	
	this.create(w, h);
};

Camera.prototype = {

	create : function(w, h) {

		this.destroy();
		
		// set viewport size
		this.halfWidth = w / 2;
		this.halfHeight = h / 2;
		
		// ensure inverse zoom is set
		this.setZoom(this.zoom);
	},
	
	destroy : function() {
		
		// NOTHING NEEDED?
	},
	
	isPointVisible : function(px, py) {
		
		if (px > this.lookAtX - this.halfWidth
		&& px < this.lookAtX + this.halfWidth
		&& py > this.lookAtY- this.halfHeight
		&& py < this.lookAtY + this.halfHeight) return true;
		
		return false;
	},
	
	isObjectVisible : function(px, py, pw, ph) {
		
		if ((px + pw) > this.lookAtX - (this.halfWidth * this.rZoom)
		&& (px - pw) < this.lookAtX + (this.halfWidth * this.rZoom)
		&& (py + ph) > this.lookAtY- (this.halfHeight * this.rZoom)
		&& (py - ph) < this.lookAtY + (this.halfHeight * this.rZoom)) return true;
		
		return false;
	},
	
	setLookAt : function(cx, cy) {
		
		this.lookAtX = cx;
		this.lookAtY = cy;
	},
	
	snapLookAt : function(scene, cx, cy) {
		
		this.lookAtX = cx;
		this.lookAtY = cy;
		
		scene.x = ((this.halfWidth * this.rZoom) - this.lookAtX) * this.zoom;
		scene.y = ((this.halfHeight * this.rZoom) - this.lookAtY) * this.zoom;
	},
	
	setZoom : function(factor) {
		
		this.zoom = factor;
		this.rZoom = 1 / this.zoom;
	},
	
	setDamping : function(d) {
		
		this.damping = d;
	},
	
	resetDamping : function() {
		
		this.damping = 20;
	},
	
	snapZoom : function(scene, factor) {
		
		this.setZoom(factor);
		
		scene.scale.x = this.zoom;
		scene.scale.y = this.zoom;
	},
	
	update : function (scene, dt) {
		
		scene.scale.x += (this.zoom - scene.scale.x) / this.damping * dt;
		scene.scale.y += (this.zoom - scene.scale.y) / this.damping * dt;
		
		scene.x += ((((this.halfWidth * this.rZoom) - this.lookAtX) * this.zoom) - scene.x) / this.damping * dt;
		scene.y += ((((this.halfHeight * this.rZoom) - this.lookAtY) * this.zoom) - scene.y) / this.damping * dt;		
	}
};
