	this.onScreenButtonDown = function(event) {

		//starts blocking tracking
		this.isBlocking = true;
		//mouse tracking event
		var offsetX = (GAME.winWidth / 2) - ((GAME.minWidth / 2) * GAME.scale);
		var offsetY = (GAME.winHeight / 2) - ((GAME.minHeight / 2) * GAME.scale);
		
		//
		this.scope.mouseBlockTracking((event.data.global.x - offsetX) * (1 / GAME.scale), (event.data.global.y - offsetY) * (1 / GAME.scale));

		//TUTORIAL 
		this.scope.swipeStart = event.data.global.y;
		//Start Tutorial if tutorial exsists
		if(_self.isTutorial)
		{
			if(_self.tutorial_counter > _self.tutorial_delay[_self.tutorial_state]){
				//Move to the next state
				//Incriments current state
				_self.tutorial_state++;
				//Sets counter timer back to 0
				_self.tutorial_counter = 0;

				//If no more tutorial states, end tutorial controls
				if(_self.tutorial_state >= _self.tutorial_delay.length){
					_self.isTutorial = false;
				}

				//Hide tut text and pointy finger
				_self.tutorial_text.visible = false;
				_self.tutorialPointer.visible = false;

				//If on last state, do not jump and display only info
				if(_self.tutorial_state == 3){
					return;
				} 
			}

			// Not waiting for input - discard it
			else {
				return;
			}
		}
	}

