	this.animateBlock = function(target, current) {

		// animating the tray movement in the most bruteforce way
		this.animateSteps = [];

		switch (current) {

			case "left":

				switch (target) {

					case "up_left":
						this.animateSteps = ["up_left"];
						break;

					case "up":
						this.animateSteps = ["up_left", "up"];
						break;

					case "up_right":
						this.animateSteps = ["up_left", "up", "up_right"];
						break;

					case "right":
						this.animateSteps = ["up_left", "up", "up_right", "right"];
						break;
				}
				break;

			case "up_left":

				switch (target) {

					case "left":
						this.animateSteps = ["left"];
						break;

					case "up":
						this.animateSteps = ["up"];
						break;

					case "up_right":
						this.animateSteps = ["up", "up_right"];
						break;

					case "right":
						this.animateSteps = ["up", "up_right", "right"];
						break;
				}
				break;

			case "up":

				switch (target) {

					case "left":

						this.animateSteps = ["up_left", "left"];
						break;

					case "up_left":
						this.animateSteps = ["up_left"];
						break;

					case "up_right":
						this.animateSteps = ["up_right"];
						break;

					case "right":
						this.animateSteps = ["up_right", "right"];
						break;
				}
				break;

			case "up_right":

				switch (target) {

					case "left":
						this.animateSteps = ["up", "up_left", "left"];
						break;

					case "up_left":
						this.animateSteps = ["up", "up_left"];
						break;

					case "up":
						this.animateSteps = ["up"];
						break;

					case "right":
						this.animateSteps = ["right"];					
						break;
				}
				break;

			case "right":

				switch (target) {

					case "left":
						this.animateSteps = ["up_right", "up", "up_left", "left"];
						break;

					case "up_left":
						this.animateSteps = ["up_right", "up", "up_left"];
						break;

					case "up":
						this.animateSteps = ["up_right", "up"];
						break;

					case "up_right":
						this.animateSteps = ["up_right"];
						break;
				}
				break;
		}

		if (this.animateSteps.length == 0) {

			this.player.setAnim(target);
			this.blockDir = target;
		}
		else {

			this.player.setAnim(this.animateSteps[0]);
			this.blockDir = this.animateSteps[0];
			this.animateSteps.shift();
		}
	}

