var FullscreenButton = function(onPress, scope) {

	// button id is useful
	this.id = 0;
	
	// on pressed function
	this.onPress = onPress;
	this.onDown = null;
	this.onUp = null;
	this.onMove = null;
	
	// function scope
	this.scope = scope;
	
	// graphics container
	this.sprite = null;
	
	// enabled flag
	this.isEnabled = true;

	// voice over file
	this.voice = null;
	this.timeout = null;
	this.isVoiceOnTabOnly = false;
	
	this.create();
};

FullscreenButton.prototype = {

	create : function() {
		
		this.destroy();
		
        this.sprite = new PIXI.Graphics();
        this.sprite.beginFill(0xFF0000);
        this.sprite.drawRect(-GAME.maxWidth / 2, -GAME.maxHeight / 2, GAME.maxWidth, GAME.maxHeight);
        this.sprite.alpha = 0;
		this.sprite.buttonMode = true;
		this.sprite.interactive = true;
		
		this.sprite
			.on('pointerdown', this.onButtonDown)
			.on('pointerup', this.onButtonUp)
			.on('pointerupoutside', this.onButtonUpOutside)
			.on('pointerover', this.onButtonOver)
			.on('pointerout', this.onButtonOut)
			.on('pointermove', this.onButtonMove);
		
		// set a ref to this scope on the sprite
		this.sprite.button = this;		
	},
	
	destroy : function() {
		
		if (this.sprite) {
			
			this.sprite
				.off('pointerdown', this.onButtonDown)
				.off('pointerup', this.onButtonUp)
				.off('pointerupoutside', this.onButtonUpOutside)
				.off('pointerover', this.onButtonOver)
				.off('pointerout', this.onButtonOut)
				.off('pointermove', this.onButtonMove);
			
			this.sprite.destroy({children:true, texture:true});
			this.sprite = null;
		}
		
		clearTimeout(this.timeout);
		this.voice = null;
	},

	setVO : function(file, tabOnly) {
		
		this.voice = file;
		this.isVoiceOnTabOnly = false || tabOnly;
	},
	
	select : function() {

		if (this.voice && !this.isVoiceOnTabOnly) {
				
			GAME.assetManager.play(this.voice);
		}
		
		// do the thing!
		if (this.onPress) {
			
			this.onPress();
		}
	},

	onButtonDown : function(event) {
		
		if (!event.currentTarget.button.isEnabled) return;
		
		//this.isOver = true;
		this.isDown = true;
		
		GAME.vibrate(250);
		
		// down handler set?
		if (event.currentTarget.button.onDown) {
			
			event.currentTarget.button.onDown(event);
		}
	},
	
	onButtonUp : function(event) {
		
		clearTimeout(event.currentTarget.button.timeout);
		
		if (!event.currentTarget.button.isEnabled) return;
		
		this.isDown = false;
		
		// down handler set?
		if (event.currentTarget.button.onUp) {
			
			event.currentTarget.button.onUp(event);
		}

		if (!this.isOver) {
			
			// only play vo if hasn't already played on over
			if (event.currentTarget.button.voice && !event.currentTarget.button.isVoiceOnTabOnly) {
				
				GAME.assetManager.play(event.currentTarget.button.voice);
			}
		}
		
		// do the thing!
		if (event.currentTarget.button.onPress) event.currentTarget.button.onPress(event);
		//GAME.assetManager.play("click");
	},
	
	onButtonUpOutside : function(event) {
		
		clearTimeout(event.currentTarget.button.timeout);
		
		if (!event.currentTarget.button.isEnabled) return;
		
		this.isDown = false;
		this.isOver = false;
	},
	
	onButtonOver : function(event) {
		
		if (!event.currentTarget.button.isEnabled) return;
		
		this.isOver = true;
		
		if (!this.isDown) {
			
			if (event.currentTarget.button.voice && !event.currentTarget.button.isVoiceOnTabOnly) {
				
				// play voice after 1 second
				var voice = event.currentTarget.button.voice;
				event.currentTarget.button.timeout = setTimeout(function(){ GAME.assetManager.play(voice) }, 750);
			}
		}
	},
	
	onButtonOut : function(event) {
		
		clearTimeout(event.currentTarget.button.timeout);
		
		if (!event.currentTarget.button.isEnabled) return;
		
		this.isOver = false;
	},
	
	onButtonMove : function(event) {
		
		if (!event.currentTarget.button.isEnabled) return;
		
		// move handler set?
		if (event.currentTarget.button.onMove) {
			
			event.currentTarget.button.onMove(event);
		}
	},
	
	setOnDown : function(callback) {
		
		// set an optional onDown callback
		this.onDown = callback;
	},

	setOnUp : function(callback) {
		
		// set an optional onUp callback
		this.onUp = callback;
	},
	
	setOnMove : function(callback) {
		
		// set an optional onMove callback
		this.onMove = callback;
	},
	
	reset : function() {
		
		this.sprite.isOver = false;
		this.sprite.isDown = false;
		
		if (this.icon) {
			
			this.icon.y = this.iconYIdle;
		}
	},
	
	enable : function() {
		
		this.isEnabled = true;
		this.sprite.buttonMode = true;
		this.sprite.interactive = true;
	},
	
	disable : function() {
		
		this.isEnabled = false;
		this.sprite.buttonMode = false;
		this.sprite.interactive = false;
	}
};
