var HidesneakEnemyGenerator = function(gameRef, container, beam_container, left, right, top) {

	this.gameRef = gameRef;
	this.left = left;
	this.right = right;
	this.top = top;
	this.container = container;
	this.beam_container = beam_container;

	this.speed = -10;

	// initial timer leaves some space for tutorial
	this.timer = 120 +(this.gameRef.isTutorial ? 250 : 0);
	this.total_timer = 0;
	this.enemies = [];
	this.beams = [];
	this.testers = [];

	this.founder = -1;
	this.founderDone = false;
	this.founderOffset = 200;

	// Warning sign
	this.warning = PIXI.Sprite.from(GAME.assetManager.get('hide_warning_sign'), false);
	this.warning.anchor.x = 0.5;
	this.warning.anchor.y = 0.5;
	this.warning.x = GAME.maxWidth/2;
	this.warning.y = GAME.maxHeight/2;
	this.warning.alpha = 0;
	this.container.addChild(this.warning);

	this.last_enemy = null;
	this.warning_distance = 600;
	this.warning_rightwards = false;
};

HidesneakEnemyGenerator.prototype = {

	destroy : function() {

		for (var i = 0 ; i < this.enemies.length; i++) {
			this.container.removeChild(this.enemies[i].sprite);
			this.enemies[i].destroy();
			this.enemies[i] = null;
		}
		this.enemies = null;

	},

	update : function (dt) {

		if (this.gameRef.game_over) {

			if (!this.founderDone) {

				var offset;
				var diff;
				// Camera is moving right to left - find its offset to a fixed point
				if (this.enemies[this.founder].scale.x == 1) {
					offset = -(this.enemies[this.founder].x -(this.gameRef.player.sprite.x +this.founderOffset)) /30;
					diff = -(this.enemies[this.founder].x -(this.gameRef.player.sprite.x +this.founderOffset));
				}
				// Camera is moving left to right
				else {
					offset = -(this.enemies[this.founder].x -(this.gameRef.player.sprite.x -this.founderOffset)) /30;
					diff = -(this.enemies[this.founder].x -(this.gameRef.player.sprite.x -this.founderOffset));
				}

				// Move camera towards the fixed point
				this.enemies[this.founder].x += offset;
				this.beams[this.founder].x += offset;

				// tween to redish color
				var o = 200;
				if (diff < 0) diff *= -1;
				if (diff > o) diff = o;
				diff /= o;
				this.beams[this.founder].tint = 0xff4444 +(0xffffff -0xff4444) *diff;

				// Mark when camera is done moving
				if (this.enemies[this.founder].x > this.gameRef.player.sprite.x +this.founderOffset) {
					if (this.enemies[this.founder].x -(this.gameRef.player.sprite.x +this.founderOffset) < 5) {
						this.founderDone = true;
					}
				}
				else {
					if ((this.gameRef.player.sprite.x +this.founderOffset) -this.enemies[this.founder].x < 5) {
						this.founderDone = true;
					}
				}
			}
			return;
		}

		this.total_timer += 1 *dt;

		this.speed = 5 +(this.total_timer /1000 *1);
		if (this.speed > 10) this.speed = 10;

		// When timer reaches 0, make a new enemy
		this.timer -= 1 *dt;
		if (this.timer <= 0) {
			
			var enemy = PIXI.Sprite.from(GAME.assetManager.get('hide_cam'), false);
			enemy.y = this.top;

			enemy.speed = -this.speed;

			// Add enemy to array
			this.enemies.push(enemy);
			this.container.addChild(enemy);

			// Add beam to the enemy
			var beam = PIXI.Sprite.from(GAME.assetManager.get('hide_cam_beam'), false);
			beam.y = enemy.y +enemy.height -20;
			this.beams.push(beam);
			this.beam_container.addChild(beam);

			if (Math.random() < 0.5) {
				enemy.x = this.right +beam.width +this.warning_distance;
				beam.x = enemy.x +15 -beam.width;

				this.warning.x = GAME.maxWidth*3/4 -100;
				this.warning_rightwards = true;
			}
			else {
				enemy.x = this.left -enemy.width -beam.width -this.warning_distance;
				enemy.scale.x = -1;
				beam.scale.x = -1;
				beam.x = enemy.x +beam.width -15;

				this.warning.x = GAME.maxWidth/4 +100;
				this.warning_rightwards = false;
			}

			this.last_enemy = enemy;
			this.warning.alpha = 1;

			// How much time until next enemy
			this.timer = 500 -(this.total_timer /20) +Math.random() *(50 -(this.total_timer /20));
			if (this.timer < 010) this.timer = 100;
		}

		// Update enemies
		this.offset(0, dt);

	}, // Update

	offset : function(offset, dt) {

		var is_self = false;
		if (offset == 0) {
			is_self = true;
		}

		// Update enemies
		for (var i = 0; i < this.enemies.length; i++) {

			var enemy = this.enemies[i];
			var beam = this.beams[i];
			var player = this.gameRef.player;
			var player_sprite = player.bounds;

			if (offset == 0) {
				enemy.x += enemy.speed *dt *(is_self? enemy.scale.x : 1);
				beam.x += enemy.speed *dt *(is_self? enemy.scale.x : 1);
			}
			else {
				enemy.x += offset *(is_self? enemy.scale.x : 1);
				beam.x += offset *(is_self? enemy.scale.x : 1);
			}

			var forgiving = 100;
			if (enemy.scale.x == -1) {
				if (player_sprite.x +player_sprite.width > beam.x -beam.width +forgiving
				&&  player_sprite.x < beam.x -forgiving
				&&  (player.currentAnim != "hide_in" || player.animProgress < 0.7) ) {
					this.gameRef.showGameOver();
					this.founder = i;
				}
			}
			else {
				if (player_sprite.x +player_sprite.width > beam.x +forgiving
				&&  player_sprite.x < beam.x + beam.width -forgiving
				&&  (player.currentAnim != "hide_in" || player.animProgress < 0.7)) {
					this.gameRef.showGameOver();
					this.founder = i;
				}
			}

			// Warning sign
			if (this.last_enemy == enemy) {

				// Warning is on the right side
				var distance = 0;
				if (this.warning_rightwards) {

					// Warning sign disappears when camera comes in screen
					if (beam.x < GAME.maxWidth*3/4) {
						this.last_enemy = null;
						this.warning.alpha = 0;
					}
					// Sign animates while camera is approaching
					else {
						distance = beam.x -GAME.maxWidth*3/4;
						distance /= this.warning_distance;

						// distance 1 -> 0
						if (distance > 0.5) {
							distance = 1 -((distance -0.5) *2);
						}
						else {
							distance *= 2;
						}
						this.warning.alpha = distance;
					}
				}
				else {
					// Warning sign disappears when camera comes in screen
					if (beam.x > GAME.maxWidth/4) {
						this.last_enemy = null;
						this.warning.alpha = 0;
					}
					// Sign animates while camera is approaching
					else {
						distance = GAME.maxWidth/4 -beam.x;
						distance /= this.warning_distance;

						// distance 1 -> 0
						if (distance > 0.5) {
							distance = 1 -((distance -0.5) *2);
						}
						else {
							distance *= 2;
						}

						this.warning.alpha = distance;
					}

				}
			}

			if ( (enemy.x +enemy.width < this.left -10 && enemy.scale.x == 1)
			||   (enemy.x -enemy.width > this.right +10 && enemy.scale.x == -1)) {
				enemy.destroy();
				beam.destroy();
				this.enemies.splice(i, 1);
				this.beams.splice(i, 1);
				i--;
			}

		} // Update enemies

	},

} // Enemy generator


