var HidesneakPlayer = function(gameRef, anim, animName) {

	this.game = gameRef;

	// graphics container
	this.sprite = null;
	
	// graphic elements
	this.armatureDisplay = null;
	
	this.animProgress = 0;
	this.currentAnim = '';
	this.animName = animName;

	this.bounds = null;

	this.isGameOver = false;

	this.shadow = null;

	this.create(anim);
};

HidesneakPlayer.prototype = {

	// Initialise animation data
	create : function(anim) {

		this.destroy();
		
		this.sprite = new PIXI.Container();
		//this.sprite.scale.x = -1;
		this.bounds = new PIXI.Rectangle(0, 0, 0, 0);
		this.bounds.x = this.sprite.x -50;
		this.bounds.y = this.sprite.y;
		this.bounds.width = 100;
		this.bounds.height = 100;

		// Shadow
		this.shadow = new PIXI.Graphics();
		this.shadow.beginFill(0, 0.3);
		this.shadow.drawEllipse(this.sprite.x -10, this.sprite.y, 40, 15);
		this.shadow.endFill();

		// Yes this is real, your eyes do not deceive you
		// Apparently if the last thing drawn with graphics has less transparency than 1
		// it causes other objects (I suspect drawn after this object) to be parcially transparent as well
		// ONLY ON ONE DEVICE - NEXUS 7 !!!
		// By drawing a 1x1 elipse outside of the screen area with full transparency, solves this issue
		this.shadow.beginFill(0, 1);
		this.shadow.drawEllipse(0, 0, 1, 1);
		this.shadow.endFill();
		this.sprite.addChild(this.shadow);

		this.initAnim(anim);

		this.vel = new Vector2D(0, 0);
		this.velNormal = new Vector2D(0, 0);
	},
	
	// Destroy armature
	destroy : function() {
		
		if (this.sprite) {
			
			if (this.armatureDisplay) {
				
				this.sprite.removeChild(this.armatureDisplay);
				this.armatureDisplay.off(dragonBones.EventObject.LOOP_COMPLETE, this.onAnimationComplete, this);
				this.armatureDisplay = null;
			}
			
			this.sprite.destroy();
			this.sprite = null;
		}

		this.vel = null;
		this.velNormal = null;
	},
	
	/* Dragonbones Animations */

	initAnim : function(id) {
		
		var factory = dragonBones.PixiFactory.factory;
		
		if (!factory._dragonBonesDataMap[id]) {
			
			factory.parseDragonBonesData(GAME.animData[0].resources[id + '_skeleton'].data);
			factory.parseTextureAtlasData(GAME.animData[0].resources[id + '_data'].data, GAME.animData[0].resources[id + '_atlas'].texture);
		}
        
		this.armatureDisplay = factory.buildArmatureDisplay(id);
		this.armatureDisplay.scale.set(1.0);
		
		this.armatureDisplay.on(dragonBones.EventObject.LOOP_COMPLETE, this.onAnimationComplete, this);
		this.setAnim(this.animName);
		
		this.sprite.addChild(this.armatureDisplay);
	},
	
	setAnim : function(anim) {
		
		if (this.armatureDisplay) {
			
			if (this.currentAnim != anim) {
				
				this.armatureDisplay.animation.play(anim);
				this.currentAnim = anim;
				
				this.armatureDisplay.animation.stop();
				this.animProgress = 0;
			}
		}
	},
	
	setFrame : function(time) {
		
		if (this.armatureDisplay) {
			
			if (this.armatureDisplay.animation._animations[this.currentAnim]) {
				
				if (time >= this.armatureDisplay.animation._animations[this.currentAnim].duration) {

					if (this.currentAnim == 'hide_in') {
						return;
					}

					if (this.currentAnim == 'hide_out') {
						if (this.game.game_over) {
							this.setAnim('idle');
						}
						else
						if (this.game.total_time > this.game.sneak_limit) {
							this.setAnim('run');
						}
						else {
							this.setAnim('sneak');
						}
					}
				}
				else
				if (time <= 0) {
					this.setAnim('idle');
				}
			}
			
			this.armatureDisplay.animation.gotoAndStopByTime(this.currentAnim, time);
		}
	},

	onAnimationComplete : function(event) {
	},
	
	pause : function() {
		
		this.isRunning = false;
		
		if (this.armatureDisplay) {
			
			
		}
	},
	
	resume : function() {
		
		this.isRunning = true;
		
		if (this.armatureDisplay) {
			
			
		}
	},
	
	//???
	hit : function(objectType) {

		
	},
	
	// Update
	update : function (dt) {

		// Player animation
		if (this.isGameOver) {

			if (this.currentAnim != 'idle' && this.currentAnim != 'hide_out') {

				this.animProgress -= dt *0.02;
			}
			else {
				this.animProgress += dt * 0.02;
			}
		}
		else {

			this.animProgress += dt * 0.02;
		}

		this.setFrame(this.animProgress);
		this.bounds.x = this.sprite.x -50;
		this.bounds.y = this.sprite.y;
	},

	hide : function() {
		this.setAnim("hide_in");
	},

	show : function() {
		this.setAnim("hide_out");
	},

}; // HideSneak Player

