var HidesneakScrollingBackground = function(container, left, top, right, bottom) {

	this.left = left;
	this.top = top;
	this.right = right;
	this.bottom = bottom;
	this.container = container;

	this.scrolling_speed = 3;

	this.logoSpacing = Math.floor(Math.random() * 3);

	var x = this.left;
	var y = this.bottom;
	this.array = [];

	this.floor_sprite = PIXI.Sprite.from(GAME.assetManager.get('hide_floor'), false);

	/* floor
	 */
	this.floor = PIXI.extras.TilingSprite.from(GAME.assetManager.get('hide_floor'), false);
	this.floor.width = GAME.maxWidth/2;
	this.floor.height = this.floor_sprite.height;
	this.floor.x = GAME.maxWidth /4;
	this.floor.y = GAME.maxHeight *3/4 -this.floor.height;
	this.floor.tilePosition.x = this.floor_sprite.width *Math.random();
	this.container.addChild(this.floor);

	/* walls
	 */
	this.wall1 = PIXI.Sprite.from(GAME.assetManager.get('hide_wall_01'), false);
	this.wall2 = PIXI.Sprite.from(GAME.assetManager.get('hide_wall_02'), false);
	this.door = PIXI.Sprite.from(GAME.assetManager.get('hide_door'), false);
	this.pillar = PIXI.Sprite.from(GAME.assetManager.get('hide_pillar'), false);

	this.door_delay = 0;
	this.pillar_delay = 0;

	/* ceiling
	 */
	this.ceiling_sprite = PIXI.Sprite.from(GAME.assetManager.get('hide_ceiling'), false);
	this.ceiling = PIXI.extras.TilingSprite.from(GAME.assetManager.get('hide_ceiling'), false);
	this.ceiling.width = GAME.maxWidth/2;
	this.ceiling.height = this.ceiling_sprite.height;
	this.ceiling.x = GAME.maxWidth /4;
	this.ceiling.y = GAME.maxHeight /4;
	this.container.addChild(this.ceiling);
	// TEST
	this.wall_array = [];

	this.wall_container = new PIXI.Container();
	this.container.addChild(this.wall_container);

	// Background
	this.bg_sprite = PIXI.Sprite.from(GAME.assetManager.get('hide_exterior'), false);
	this.wall_container.addChild(this.bg_sprite);

	// Keep adding walls until they pass the screen
	do {
		this.add_wall();
		x += this.wall_array[this.wall_array.length-1].width;
	} while (this.wall_array[this.wall_array.length-1].x +this.wall_array[this.wall_array.length-1].width < GAME.maxWidth*3/4);

	/* camera rail
	 */
	this.camera_rail_sprite = PIXI.Sprite.from(GAME.assetManager.get('hide_camera_rail'), false);
	this.camera_rail = PIXI.extras.TilingSprite.from(GAME.assetManager.get('hide_camera_rail'), false);
	this.camera_rail.width = GAME.maxWidth/2;
	this.camera_rail.height = this.camera_rail_sprite.height;
	this.camera_rail.x = GAME.maxWidth /4;
	this.camera_rail.y = this.ceiling.y +this.ceiling.height;//GAME.maxHeight/4
	this.container.addChild(this.camera_rail);

	// Background
	this.bg_sprite.width = GAME.maxWidth/2;
	this.bg_sprite.height = this.wall_array[0].height -50;
	this.bg_sprite.x = GAME.maxWidth/4;
	this.bg_sprite.y = this.wall_array[0].y;
};

HidesneakScrollingBackground.prototype = {

	destroy : function() {
	},
	
	update : function (dt) {
	},

	offset : function(offset) {

		// Floor
		this.floor.tilePosition.x += offset;
		if (this.floor.tilePosition.x < 0) this.floor.tilePosition.x += this.floor_sprite.width;

		// The game should scroll backwards, should it?
		//if (this.floor.tilePosition.x > this.floor_sprite.width) this.floor.tilePosition.x -= this.floor_sprite.width;

		// Ceiling
		this.ceiling.tilePosition.x += offset;
		if (this.ceiling.tilePosition.x < 0) this.ceiling.tilePosition.x += this.ceiling_sprite.width;

		// The game should scroll backwards, should it?
		//if (this.ceiling.tilePosition.x > this.ceiling_sprite.width) this.ceiling.tilePosition.x -= this.ceiling_sprite.width;
		
		// Camera rail
		this.camera_rail.tilePosition.x += offset;
		if (this.camera_rail.tilePosition.x < 0) this.camera_rail.tilePosition.x += this.camera_rail_sprite.width;

		// Walls
		for (var i = 0; i < this.wall_array.length; i++) {
			var wall = this.wall_array[i];

			wall.x += offset;
			if (wall.x +wall.width < GAME.maxWidth/4) {
				this.wall_array.splice(i, 1);
				wall.destroy();
			}
		}
		if (this.wall_array[this.wall_array.length-1].x +this.wall_array[this.wall_array.length-1].width < GAME.maxWidth*3/4) {
			this.add_wall();
		}

		if (this.door_delay > 0) {
			this.door_delay += offset;
			if (this.door_delay < 0) {
				this.door_delay = 0;
			}
		}

		if (this.pillar_delay > 0) {
			this.pillar_delay += offset;
			if (this.pillar_delay < 0) {
				this.pillar_delay = 0;
			}
		}
	},

	getGroundLine : function() {return this.floor.y +this.floor.height/2;},
	getRailLine : function() {return this.camera_rail.y +this.camera_rail.height/15;},

	// Add a wall to the array
	add_wall : function() {

		var x;
		if (this.wall_array.length == 0) {
			x = GAME.maxWidth/4 -(this.wall1.width *Math.random());
		}
		else {
			x = this.wall_array[this.wall_array.length-1].x +this.wall_array[this.wall_array.length-1].width;
		}

		// reduce the logo spcaing count which ensures WARPA logos
		// don't appear to close to eachother
		this.logoSpacing--;

		if (this.logoSpacing == 0) {
			
			sprite = PIXI.Sprite.from(GAME.assetManager.get('hide_wall_03'), false);
			this.logoSpacing = 4;
		}
		else {

			// Pick the sprite for the wall randomly
			var sprite;
			var r = Math.random();
			if (r < 0.25 && this.door_delay == 0) {

				sprite = PIXI.Sprite.from(GAME.assetManager.get('hide_door'), false);
				this.door_delay = this.door.width *2;
			}
			else
			if (r < 0.4) {

				sprite = PIXI.Sprite.from(GAME.assetManager.get('hide_wall_01'), false);
			}
			else
			if (r < 0.6) {

				sprite = PIXI.Sprite.from(GAME.assetManager.get('hide_wall_02'), false);
			}
			else
			if (r < 0.8) {
				sprite = PIXI.Sprite.from(GAME.assetManager.get('hide_wall_05'), false);
			}
			else
			{
				sprite = PIXI.Sprite.from(GAME.assetManager.get('hide_wall_04'), false);
			}
		}

		// Resize wall to fit floor/ceiling while keeping aspect ratio
		var aspect = sprite.width/sprite.height;
		sprite.height = this.floor.y -(this.ceiling.y +this.ceiling.height);
		sprite.width = sprite.height *aspect;
		sprite.x = x - 1;	// <-- attempt to fix seaming on some devices
		sprite.y = this.floor.y -sprite.height;
		this.wall_container.addChild(sprite);
		this.wall_array.push(sprite);

		// Sometimes, add a pillar after the wall
		if (Math.random() < 0.3 && this.pillar_delay == 0) {
			var pillar = PIXI.Sprite.from(GAME.assetManager.get('hide_pillar'), false);
			var paspect = pillar.width/pillar.height;
			pillar.height = sprite.height;
			pillar.width = pillar.height *paspect;
			pillar.x = sprite.x +sprite.width - 1; // <-- attempt to fix seaming on some devices
			pillar.y = sprite.y;
			this.wall_container.addChild(pillar);
			this.wall_array.push(pillar);

			this.pillar_delay = this.pillar.width *2 +(Math.random() *this.pillar.width *3);
		}



	},

}

