/*
 * Animated spinning stars
 * DD 2018
 */

var KOStars = function() {
	
	// graphics container
	this.sprite = null;
	
	// graphic elements
	this.stars = null;
	
	this.create();
};

KOStars.prototype = {

	create : function() {
		
		this.destroy();
		
		this.sprite = new PIXI.Container();
		
		var frames = [];
		var texture;
		var scale = 1;

		texture = PIXI.Texture.from(GAME.assetManager.get('ko_stars', false));
		
		var px = 0;
		var py = 0;
		
		for (var i = 0; i < 17; i++) {
			
			frames.push(new PIXI.Texture(texture, new PIXI.Rectangle((128 * scale) * px, (59 * scale) * py, 128 * scale, 59 * scale)));
			
			px++;
			
			if (px > 3) {
				
				px = 0;
				py++;
				
				
				//if (py > 2) break;
			}
		}
		
		this.stars = new PIXI.extras.AnimatedSprite(frames);
		this.stars.anchor.set(0.5);
		this.stars.scale.set(1 / scale);
		this.stars.animationSpeed = 0.5;
		this.stars.play();
		this.sprite.addChild(this.stars);
	},
	
	destroy : function() {
		
		if (this.sprite) {
			
			this.sprite.removeChild(this.stars);
			this.stars = null;
			
			this.sprite = null;
		}
	},
	
	pause : function() {
		
		this.stars.stop();
	},
	
	play : function() {
		
		this.stars.play();
	},
	
	update : function(dt) {
		
		
	}
};
