var RunningTrack = function(gameRef) {

	this.game = gameRef;

	// graphics container
	this.sprite = null;
	
	// list of pieces
	this.pieces = null;

	// current movement direction
	this.currentDirection = 0;
	this.lastDirection = 0;

	// player movement direction
	this.playerDirection = 0;

	// direction definitions
	this.directions = [{x: 0, y:-1}, {x:-1, y: 0}, {x: 1, y: 0}];

	// this goes up as the player progresses
	// starts at zero so the road is straight during intro
	this.directionChangeChance = 0;

	this.lastPieceX = 0;
	this.lastPieceY = 0;

	this.totalPieces = 0;

	this.isLastPieceDirectionChanged = false;

	// player container - this is transformed to reverse
	// the transformation of the track so that the player
	// can be contained within the track but not distorted
	this.playerHolder = null;

	this.particleHolder = null;

	// list of turns to make
	this.turnList = null;
	this.turnPieces = null;
	
	// player speed
	this.speed = 10;
	
	// distance travelled
	this.distance = 0;

	// add/remove a tile every time we travel the width of one
	this.nextPieceAt = 512;

	// enemy van container - works the same as the player holder
	this.enemyHolder = null;

	// van follows these wwaypoints left by the player
	this.enemyWaypoints = null;
	this.currentEnemyWaypoint = -1;

	this.objects = null;

	this.create();
};

RunningTrack.prototype = {

	create : function() {

		this.destroy();
		
		this.sprite = new PIXI.Container();

		this.holder = new PIXI.Container();
		this.holder.rotation = Math.PI / 4;
		this.sprite.addChild(this.holder);

		this.particleHolder = new PIXI.Container();
		//this.particleHolder.rotation = Math.PI / 4;
		this.particleHolder.alpha = 0;
		this.holder.addChild(this.particleHolder);

		this.sprite.scale.y = 0.5;
		
		// turn list
		this.turnList = [];

		// all tiles
		this.pieces = [];

		// tiles on a turn
		this.turnPieces = [];

		// scene objects
		this.objects = [];

		// build a chunk straight away
		for (var i = 0; i < 20; i++) {

			this.addPiece();
		}

		this.playerHolder = new PIXI.Container();
		this.playerHolder.rotation = -Math.PI / 4;
		this.playerHolder.scale.y = 2;
		this.holder.addChild(this.playerHolder);

		// start the player up the road a bit
		this.playerHolder.x = this.pieces[5].sprite.x;
		this.playerHolder.y = this.pieces[5].sprite.y;

		// chasing enemy
		this.enemyHolder = new PIXI.Container();
		this.enemyHolder.rotation = -Math.PI / 4;
		this.enemyHolder.scale.y = 2;
		this.holder.addChild(this.enemyHolder);

		this.enemyHolder.x = this.pieces[4].sprite.x;
		this.enemyHolder.y = this.pieces[4].sprite.y;

		this.enemyWaypoints = [];

		// DEBUG
		/*
		var graphics = new PIXI.Graphics();
		graphics.beginFill(0x0000FF, 1);
		graphics.drawRect(-10, -10, 20, 20);
		graphics.endFill();
		this.playerHolder.addChild(graphics);
		*/
	},
	
	destroy : function() {
		
		if (this.pieces) {

			while (this.pieces.length > 0) {

				this.pieces[0].destroy();
				this.pieces[0] = null;
				this.pieces.splice(0, 1);
			}

			this.pieces = null;
		}

		if (this.sprite) {	
			
			if (this.holder) {

				this.sprite.removeChild(this.holder);

				this.holder.removeChildren();
				this.holder.destroy();
				this.holder = null;
			}

			this.sprite.destroy();
			this.sprite = null;
		}
	},

	addPiece : function() {

		// place piece based on last direction
		var piece = new RunningTrackPiece(this.lastDirection + '_' + this.currentDirection);
		piece.sprite.x = this.lastPieceX + (this.directions[this.lastDirection].x * 512);
		piece.sprite.y = this.lastPieceY + (this.directions[this.lastDirection].y * 512);

		this.lastPieceX = piece.sprite.x;
		this.lastPieceY = piece.sprite.y;

		this.holder.addChild(piece.sprite);
		this.pieces.push(piece);

		// cap the amount of pieces we have at one time
		if (this.pieces.length > 24) {

			this.removePiece();
		}

		// record this for game progression
		this.totalPieces++;

		// as the game goes on - corners are more likely/frequent
		if (this.totalPieces > 80) {

			this.directionChangeChance = 0.65;
		}
		else if (this.totalPieces > 64) {

			this.directionChangeChance = 0.6;
		}
		else if (this.totalPieces > 48) {

			this.directionChangeChance = 0.55;
		}
		else if (this.totalPieces > 32) {

			this.directionChangeChance = 0.5;
		}
		else if (this.totalPieces > 16) {

			this.directionChangeChance = 0.45;
		}
		else if (this.totalPieces > 8) {

			this.directionChangeChance = 0.4;
		}

		// choose next direction
		this.lastDirection = this.currentDirection;

		// cannot change direction twice in a row
		if (this.isLastPieceDirectionChanged) {

			this.isLastPieceDirectionChanged = false;
			this.turnPieces.push(piece);
		}
		else {

			// decide new direction
			var r = Math.random();

			// start is always the same - staright for X pieces
			// then a guaranteed turn
			if (this.totalPieces < 13) r = 1;
			
			if (this.totalPieces == 10) r = 0;
			else if (this.totalPieces == 13) r = 0;

			if (r < this.directionChangeChance) {

				// from dir 1 we can go to 2 or 3
				// equal chance
				if (this.currentDirection == 0) {

					this.currentDirection = Math.floor(Math.random() * 2) + 1;
				}
				// from 2 or 3 we can only go back to 1
				else {

					this.currentDirection = 0
				}

				this.turnList.push(this.currentDirection);
				this.isLastPieceDirectionChanged = true;
			}
		}

		// add dressing objects
		if (piece.object) {

			if (!this.isLastPieceDirectionChanged) {
			
				piece.object.x += piece.sprite.x;
				piece.object.y += piece.sprite.y;
				this.objects.push(piece.object);
				this.holder.addChild(piece.object);
			}
			else {

				piece.object.destroy();
				piece.object = null;
			}
		}

		if (piece.tree) {

			piece.tree.x += piece.sprite.x;
			piece.tree.y += piece.sprite.y;
			this.objects.push(piece.tree);
			this.holder.addChild(piece.tree);
		}

		if (piece.smallObject) {

			piece.smallObject.x += piece.sprite.x;
			piece.smallObject.y += piece.sprite.y;
			this.objects.push(piece.smallObject);
			this.holder.addChild(piece.smallObject);
		}
	},

	removePiece : function() {

		var piece = this.pieces[0];
		this.pieces.shift();

		for (var i = 0; i < this.objects.length; i++) {

			if (this.objects[i] == piece.object) {

				this.objects.splice(i, 1);
				this.holder.removeChild(this.objects[i]);
				break;
			}
		}

		for (var i = 0; i < this.objects.length; i++) {

			if (this.objects[i] == piece.tree) {

				this.objects.splice(i, 1);
				this.holder.removeChild(this.objects[i]);
				break;
			}
		}

		this.holder.removeChild(piece.sprite);
		piece.destroy();
		piece = null;
	},
	
	nextPlayerDirection : function() {

		if (this.turnList[0] == 0) {

			this.game.player.vel.x = 0;
			this.game.player.vel.y = -1;
			this.playerHolder.scale.x = 1;
		}
		else if (this.turnList[0] == 1) {

			// left
			this.game.player.vel.x = -1;
			this.game.player.vel.y = 0;
			this.playerHolder.scale.x = -1;
		}
		else if (this.turnList[0] == 2) {

			// right
			this.game.player.vel.x = 1;
			this.game.player.vel.y = 0;
			this.playerHolder.scale.x = 1;
		}

		//this.playerDirection = this.turnList[0];
		this.turnList.shift();

		this.turnPieces[0].onTurned();
		
		var tx = this.turnPieces[0].sprite.x;
		var ty = this.turnPieces[0].sprite.y;

		this.turnPieces.shift();
		
		var wx = this.playerHolder.x;
		var wy = this.playerHolder.y;

		this.enemyWaypoints.unshift({x: tx, y: ty});
	},

	hidePrompts : function() {

		for (var i = 0; i < this.turnPieces.length; i++) {

			this.turnPieces[i].hidePrompt();
		}
	},

	setEnemyVelocity : function (enemy) {

		// head for the last waypoint in the array
		if (this.enemyWaypoints.length > 0) {

			enemy.vel.x = this.enemyWaypoints[this.enemyWaypoints.length - 1].x - this.enemyHolder.x;
			enemy.vel.y = this.enemyWaypoints[this.enemyWaypoints.length - 1].y - this.enemyHolder.y;
			enemy.vel.normalise();
		}
		// just head for the player
		else {

			enemy.vel.x = this.playerHolder.x - this.enemyHolder.x;
			enemy.vel.y = this.playerHolder.y - this.enemyHolder.y;
			enemy.vel.normalise();
		}

		enemy.setRotation();

		if (Math.abs(enemy.vel.x) > Math.abs(enemy.vel.y)) {
			
			enemy.vel.y = 0;
			
			if (enemy.vel.x > 0) enemy.vel.x = 1;
			else enemy.vel.x = -1;
		}
		else {
			
			enemy.vel.x = 0;
			enemy.vel.y = -1;
		}
	},

	updateEnemyWaypoints : function (enemy) {

		// not chasing the player (the player is never reached)
		if (this.enemyWaypoints.length > 0) {

			if (COLLISIONS.circle(this.enemyHolder.x, this.enemyHolder.y, 16, this.enemyWaypoints[this.enemyWaypoints.length - 1].x, this.enemyWaypoints[this.enemyWaypoints.length - 1].y, 10) < 0) {

				// remove the last waypoint
				this.enemyWaypoints.pop();
			}
		}

		this.setEnemyVelocity(enemy);
	},

	zSort : function() {
		
		var sortables = [];
		
		//sortables.push(this.particleHolder);
		sortables.push(this.playerHolder);
		sortables.push(this.enemyHolder);

		// add objects
		for (var i = 0; i < this.objects.length; i++) {
			
			sortables.push(this.objects[i]);
		}

		// sort the array
		sortables.sort(function(a, b) {
			
			if (a.y < b.y) return -1;
			if (a.y > b.y) return 1;
			
			return 0;
		});
		
		this.holder.addChild(this.particleHolder);

		// re-add to display list
		for (var i = 0; i < sortables.length; i++) {
			
			this.holder.addChild(sortables[i]);
		}
	},

	updateEnemy : function(dt, enemy, isGameOver) {

		var isMove = true;

		if (isGameOver) {

			if (COLLISIONS.circle(this.playerHolder.x, this.playerHolder.y, 200, this.enemyHolder.x, this.enemyHolder.y, 1) < 0) {

				isMove = false;
			}

			this.speed *= 0.95;
		}
		
		if (isMove) {
		
			if (COLLISIONS.circle(this.playerHolder.x, this.playerHolder.y, 600, this.enemyHolder.x, this.enemyHolder.y, 1) < 0) {

				// make sure we don't overtake or get too close
				this.enemyHolder.x += enemy.vel.x * this.speed * dt * 0.92;
				this.enemyHolder.y += enemy.vel.y * this.speed * dt * 0.92;
			}
			else {
				
				// enemy (x0.99 speed = frankie gradually gets further away - but never escapes)
				this.enemyHolder.x += enemy.vel.x * this.speed * dt * 1.05;// * 0.999;
				this.enemyHolder.y += enemy.vel.y * this.speed * dt * 1.05;// * 0.999;
			}
		}

		this.updateEnemyWaypoints(enemy);
	},

	update : function (dt, player, enemy) {
		
		if (this.distance > this.nextPieceAt) {

			this.addPiece();
			this.nextPieceAt += 512;
		}

		// check we're still on the track
		var isOnTrack = false;
		for (var i = 0; i < this.pieces.length; i++) {

			if (this.pieces[i].checkCollision(this.playerHolder.x, this.playerHolder.y)) {

				isOnTrack = true;
				break;
			}
		}

		// off the track - game over!
		if (!isOnTrack) {

			this.game.onGameOver();
			return;
		}

		// player movement
		this.playerHolder.x += this.game.player.vel.x * this.speed * dt;
		this.playerHolder.y += this.game.player.vel.y * this.speed * dt;

		this.game.player.emitter.updateSpawnPos(this.playerHolder.x, this.playerHolder.y);

		this.distance += this.speed * dt;
		
		// increase speed over time
		if (this.speed < 32) this.speed += 0.005;

		if (this.speed > 18) {

			if (this.speed < 20) {
			
				this.particleHolder.alpha = (this.speed - 18) * 0.5;
			}
			else {

				this.particleHolder.alpha = 1;
			}
		}
	}
};
