/*
 * Scene Layer for parallax scrolling 
 * DD 2017
 */

var SceneLayer = function(factor, loopAt) {
	
	// graphics container
	this.sprite = null;
	
	// parallax scrolling factor
	this.factor = factor;
	
	// looping
	this.scrollTotal = 0;
	this.loopAt = loopAt || 0;
	
	// total width
	this.width = 0;
	
	// front layer?
	this.isFront = false;
	
	this.create();
};

SceneLayer.prototype = {

	create : function() {
		
		this.sprite = new PIXI.Container();
	},
	
	destroy : function() {
		
		for (var i = 0; i < this.sprite.children.length; i++) {
			
			this.sprite.removeChild(this.sprite.children[i]);
			this.sprite.children[i] = null;
		}
		
		this.sprite = null;
		
		this.width = 0;
	},
	
	add : function(asset, x, y, scale) {
		
		// add an asset to the layer
		var part = PIXI.Sprite.from(GAME.assetManager.get(asset, false));
		part.x = this.width;//x;
		part.y = y;
		
		if (scale) part.scale.set(scale);
		this.sprite.addChild(part);
		
		this.width += part.width;
	},
	
	addSpace : function(space) {
		
		this.width += space;
	},
	
	update : function (container, dt) {
		
		// set position based on parallax factor
		this.sprite.x = (container.x * this.factor) - container.x + this.scrollTotal;

		// looping
		if ((container.x * this.factor * (1 / container.scale.x)) + this.scrollTotal < -this.loopAt) {

			this.scrollTotal += this.loopAt;
		}

		// set front layer to front by re-adding
		if (this.isFront) {
					
			container.addChild(this.sprite);
		}

		// set visibility based on each asset position
		
	}
};
var ScreenShake = function() {
	
	// shake properties
	this.dx = 0;
	this.dy = 0;
	
	this.f = 1.0;
	this.r = 0.75;
	
	// reference to shake clip
	this.sprite = null;
	
	// original position of clip
	this.sx = 0;
	this.sy = 0;
	
	// shaking flag
	this.isShaking = false;
};

ScreenShake.prototype = {
	
	shake : function(dx, dy, sprite) {
		
		// only shake if not already shaking
		if (!this.isShaking) {
			
			// reference supplied clip
			this.sprite = sprite;
			
			// save original position
			this.sx = this.sprite.x;
			this.sy = this.sprite.y;
			
			// set shake properties
			this.dx = dx;
			this.dy = dy;
			this.f = 1;
			
			this.isShaking = true;
		}
	},
	
	setReduction : function(r) {
		
		this.r = r;
	},

	update : function(dt) {
		
		// update if shaking
		if (this.isShaking) {

			// update clip position
			this.sprite.x = this.sx + (this.dx * this.f * (GAME.app.ticker.elapsedMS * 0.1));	
			this.sprite.y = this.sy + (this.dy * this.f * (GAME.app.ticker.elapsedMS * 0.1));	
			
			// reduce shake factor
			this.f *= (-1 * this.r/* * dt*/);
			
			// has the shaker come to rest
			if (Math.abs(this.f) < 0.1) {
				
				// stop shaking
				this.isShaking = false;
				
				// reset position
				this.sprite.x = this.sx;
				this.sprite.y = this.sy;
			}
		}
	}
};
