    RecycleScene.prototype.startTutorial = function () {
        this.tutorialMode = true;
        this.nextTutorialStep();
        this.addListeners();
        for (var hitzone in this.hitZones)
            this.hitZones[hitzone].enable();
    };
    RecycleScene.prototype.addNewJunk = function () {
        var _this = this;
        var g = 0;
        var m = 0;
        var c = 0;
        game_1.Game.coreData.playerData.newJunk.forEach(function (junkData) {
            var angle = game_1.Game.getRandom(0, 360);
            var type = gameplay_level_data_1.LevelData.junkProps[junkData].type;
            if (type == 'metal') {
                var pos = [140, 100 * -m];
                m++;
            }
            if (type == 'glass') {
                var pos = [270, 100 * -g];
                g++;
            }
            if (type == 'card') {
                var pos = [400, 100 * -c];
                c++;
            }
            var junk = new gameplay_junk_1.Junk(_this, { id: junkData, angle: angle, pos: pos });
            _this.junkArray.push(junk);
            _this.junkLayer.addChild(junk.container);
        });
        gsap_1.gsap.delayedCall(1.2, function () {
            if (game_1.Game.coreData.playerData.newJunk.length < 7)
                game_1.Game.playSound('junk-smash-small-' + game_1.Game.getRandom(1, 3, true) + '.mp3');
            else
                game_1.Game.playSound('junk-smash-big-' + game_1.Game.getRandom(1, 3, true) + '.mp3');
        }.bind(this));
        gsap_1.gsap.delayedCall(1.2, function () { this.junkAdded = true; }.bind(this));
    };
    RecycleScene.prototype.spawnJunk = function (_id, _amount) {
        var g = 0;
        var m = 0;
        var c = 0;
        var amount = _amount || 1;
        for (var i = 0; i < amount; i++) {
            var angle = game_1.Game.getRandom(0, 360);
            var type = gameplay_level_data_1.LevelData.junkProps[_id].type;
            if (type == 'metal') {
                var pos = [140, 100 * -m];
            }
            if (type == 'glass') {
                var pos = [270, 100 * -g];
                g++;
            }
            if (type == 'card') {
                var pos = [400, 100 * -c];
                c++;
            }
            var junk = new gameplay_junk_1.Junk(this, { id: _id, angle: angle, pos: pos });
            this.junkArray.unshift(junk);
            this.junkLayer.addChild(junk.container);
        }
        this.delayedSort = gsap_1.gsap.delayedCall(1, function () {
            if (this.destroyed)
                return;
            this.junkArray.sort(function (a, b) { return (a.p2Body.position[1] > b.p2Body.position[1]) ? 1 : -1; });
            game_1.Game.playSound('junk-smash-small-' + game_1.Game.getRandom(1, 3, true) + '.mp3', 0.7);
        }.bind(this));
    };
