function resizeCanvas() {
    var tempInnerWidth = window.innerWidth;
    var tempInnerHeight = window.innerHeight;
    canvas.height = tempInnerHeight;
    canvas.width = tempInnerWidth;
    canvas.style.width = tempInnerWidth + "px";
    canvas.style.height = tempInnerHeight + "px";
    if(tempInnerWidth > tempInnerHeight) {
        if(canvas.height < minSquareSize) {
            canvas.height = minSquareSize;
            canvas.width = minSquareSize * (tempInnerWidth / tempInnerHeight);
            canvasScale = minSquareSize / tempInnerHeight;
        } else if(canvas.height > maxSquareSize) {
            canvas.height = maxSquareSize;
            canvas.width = maxSquareSize * (tempInnerWidth / tempInnerHeight);
            canvasScale = maxSquareSize / tempInnerHeight;
        } else {
            canvasScale = 1;
        }
    } else {
        if(canvas.width < minSquareSize) {
            canvas.width = minSquareSize;
            canvas.height = minSquareSize * (tempInnerHeight / tempInnerWidth);
            canvasScale = minSquareSize / tempInnerWidth;
        } else if(canvas.width > maxSquareSize) {
            canvas.width = maxSquareSize;
            canvas.height = maxSquareSize * (tempInnerHeight / tempInnerWidth);
            canvasScale = maxSquareSize / tempInnerWidth;
        } else {
            canvasScale = 1;
        }
    }
    switch(gameState) {
        case "game":
            if(isMobile) {
                userInput.addHitArea("gameTouch", butEventHandler, {
                    isDraggable: true,
                    multiTouch: true
                }, "rect", {
                    aRect: [
                        0, 
                        50, 
                        canvas.width, 
                        canvas.height
                    ]
                }, true);
            }
            break;
        case "start":
            break;
        case "credits":
            break;
        case "charSelect":
            panel.updateCharSelectButs();
            break;
        case "progress":
            break;
        case "gameIntro":
            break;
        case "pause":
            break;
        case "tournamentWin":
            break;
        case "gameComplete":
            break;
    }
    prevCanvasWidth = tempInnerWidth;
    prevCanvasHeight = tempInnerHeight;
    window.scrollTo(0, 0);
}
function initRotateWarning() {
    isRotated = true;
    prevGameState = gameState;
    gameState = "rotated";
    background = new Elements.Background();
    previousTime = new Date().getTime();
    resizeCanvas();
    updateRotateWarningEvent();
}
function initBackFromRotate() {
    isRotated = false;
    switch(prevGameState) {
        case "start":
            gameState = "start";
            previousTime = new Date().getTime();
            updateStartScreenEvent();
            break;
        case "start":
            gameState = "game";
            previousTime = new Date().getTime();
            updateGameEvent();
        case "pause":
            gameState = "pause";
            previousTime = new Date().getTime();
            updatePauseEvent();
            break;
        case "credits":
            gameState = "credits";
            previousTime = new Date().getTime();
            updateCreditsScreenEvent();
            break;
        case "charSelect":
            gameState = "charSelect";
            previousTime = new Date().getTime();
            updateCharSelectScreenEvent();
            break;
        case "progress":
            gameState = "progress";
            previousTime = new Date().getTime();
            updateCharSelectScreenEvent();
            break;
        case "gameIntro":
            gameState = "gameIntro";
            previousTime = new Date().getTime();
            updateGameIntroScreenEvent();
            break;
        case "gameComplete":
            gameState = "gameComplete";
            previousTime = new Date().getTime();
            updateGameComplete();
            break;
        case "game":
            gameState = "game";
            previousTime = new Date().getTime();
            updateGameEvent();
            break;
        case "tournamentWin":
            gameState = "tournamentWin";
            previousTime = new Date().getTime();
            updateGameEvent();
            break;
    }
}
function playSound(_id) {
    if(audioType == 1) {
        sound.play(_id);
    }
}
function toggleMute() {
    muted = !muted;
    if(audioType == 1) {
        if(muted) {
            Howler.mute(true);
            music.pause();
        } else {
            Howler.mute(false);
            playMusic();
            if(gameState == "game") {
                music.volume(.18);
            } else {
                music.volume(.3);
            }
        }
    } else if(audioType == 2) {
        if(muted) {
            music.pause();
        } else {
            playMusic();
        }
    }
}
